import React, { Fragment, useState, useEffect } from 'react'
import MetaData from '../../layout/MetaData'
import { useDispatch, useSelector } from 'react-redux'
import { getProduct } from '../../../actions/product.actions'
import Product from '../ProductList/Product.List'
import Loader from '../../layout/Loader/Loader'
import { useAlert } from 'react-alert'
import './ProductDisplay.scss'
import Pagenation from 'react-js-pagination'

const Products = () => {
    const [currentPage, setCurrentPage] = useState(1)


    const dispatch = useDispatch();
    const { loading, products, error, productCount } = useSelector(state => state.products)
    const alert = useAlert()

    useEffect(() => {
        if (error) {
            return alert.error(error)
        }
        dispatch(getProduct())
    }, [dispatch, alert, error])

    function setCurrentPageNo() {
        setCurrentPage(pageNumber)
    }


    return (
        <div className="product-display-content">
            {loading ? <Loader /> : (
                <Fragment>
                    <MetaData title={''} />
                    <h1 id="products_heading">Latest Products</h1>
                    <div className="list-product">
                        {products && products.map(product => (
                            <Product key={product._id} product={product} />
                        ))}
                    </div>
                    <div className="d-flex justify-content-center mt-5">
                        <Pagenation
                            activePage={currentPage}
                            itemsCountPerPage={resPerPage}
                            totalItemsCount={productCount}
                            onChange={setCurrentPageNo}
                            nextPageText={'Next'}
                            prevPageText={'Prev'}
                            firstPageText={'First'}
                            lastPageText={'Last'}
                            itemClass="page-item"
                            linkClass="page-link"
                        />
                    </div>
                </Fragment>
            )}
        </div>
    )
}

export default Products
