import React, { Fragment, useState, useEffect } from 'react'
import { Link } from 'react-router-dom'

import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux'

import { updateProfile, loadUser, clearError } from '../../actions/userAction'
import MetaData from '../layout/MetaData'

import { userConstants } from '../../constants/userConstant'

const UpdateProfile = ({ history }) => {

    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [avatar, setAvatar] = useState('')
    const [avatarPreview, setAvatarPreview] = useState('/images/download.jpg')

    const alert = useAlert()
    const dispatch = useDispatch()

    const { user } = useSelector(state => state.auth)
    const { error, isUpdated, loading } = useSelector(state => state.user)

    useEffect(() => {
        if (user) {
            setName(user.name)
            setEmail(user.email)
            setAvatarPreview(user.avatar.url)
        }
        if (error) {
            alert.error(error);
            dispatch(clearError())
        }
        if (isUpdated) {
            alert.success('Update User Berhasil')
            dispatch(loadUser())
            history.push('/Profile')
            dispatch({
                type: userConstants.UPDATE_PROFILE_RESET
            })
        }
    }, [dispatch, alert, error, history, isUpdated])

    const submitHandler = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.set('name', name);
        formData.set('email', email);
        formData.set('avatar', avatar);
        dispatch(updateProfile(formData))
    }

    const onChange = e => {
        const reader = new FileReader();
        reader.onload = () => {
            if (reader.readyState === 2) {
                setAvatarPreview(reader.result)
                setAvatar(reader.result)
            }
        }
        reader.readAsDataURL(e.target.files[0])
    }


    return (
        <Fragment>
            <MetaData title={'Update Profile'} />
            <div className="Profile">
                <div className="upper-profile">
                    <img src={user.avatar.url} alt={user.name} />
                </div>
                <div className="content-profile">
                    <div className="list-profile">
                        <ul>
                            <li>
                                <p>Nama Pengguna</p>
                                <a>{user.name}</a>
                            </li>
                            <hr />
                            <li>
                                <p>E-mail</p>
                                <a>{user.email}</a>
                            </li>
                            <hr />
                            <li>
                                <p>Gabung</p>
                                <a>{String(user.createdAt).substring(0, 10)}</a>
                            </li>
                        </ul>
                    </div>
                </div>
                <div className="sidebar-profile">
                    <img src={user.avatar.url} alt={user.name} />
                    <div className="control-profile">
                        <button className="ganti-profile">
                            Simpan Profile
                        </button>
                    </div>
                </div>
            </div>
        </Fragment>
    )
}

export default UpdateProfile
