import { orderConstant } from "../constants/orderConstant";

export const newOrderReducer = (state = {}, action) => {
    switch (action.type) {
        case orderConstant.CREATE_ORDER_REQUEST:
            return {
                ...state,
                loading: true,
            }
        case orderConstant.CREATE_ORDER_SUCCESS:
            return {
                loading: false,
                order: action.payload,
            }
        case orderConstant.CREATE_ORDER_FAILURE:
            return {
                loading: false,
                error: action.payload
            }
        case orderConstant.CLEAR_ERROR:
            return {
                ...state,
                error: null
            }
        default:
            return state;
    }
}


export const myOrdersReducer = (state = { order: [] }, action) => {
    switch (action.type) {

        case orderConstant.ORDER_DETAILS_REQUEST:
            return {
                loading: true,
            }

        case orderConstant.ORDER_DETAILS_SUCCESS:
            return {
                loading: false,
                order: action.payload
            }
        case orderConstant.ORDER_DETAILS_FAILURE:
            return {
                loading: false,
                error: action.payload
            }
        case orderConstant.CLEAR_ERROR:
            return {
                ...state,
                error: null
            }
        default:
            return state
    }
}


export const orderDetailsReducer = (state = { orders: {} }, action) => {
    switch (action.type) {

        case orderConstant.MY_ORDERS_REQUEST:
            return {
                loading: true,
            }

        case orderConstant.MY_ORDERS_SUCCESS:
            return {
                loading: false,
                orders: action.payload
            }
        case orderConstant.MY_ORDERS_FAILURE:
            return {
                loading: false,
                error: action.payload
            }
        case orderConstant.CLEAR_ERROR:
            return {
                ...state,
                error: null
            }
        default:
            return state
    }
}
