import React, { Fragment, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { MDBDataTable } from 'mdbreact'
import Loader from '../layout/Loader'
import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux';
import { getAdminsProducts, clearError } from '../../actions/product.actions'
import { FiTrash, FiEdit } from "react-icons/fi";
import MetaData from '../layout/MetaData';
import Sidebar from './Sidebar'


const ProductList = ({ history }) => {

    const alert = useAlert()
    const dispatch = useDispatch()

    const { loading, error, products } = useSelector(state => state.products)

    useEffect(() => {
        dispatch(getAdminsProducts())

        if (error) {
            alert.error(error)
            dispatch(clearError())
        }
    }, [dispatch, alert, error])

    const setProducts = () => {
        const data = {
            columns: [
                {
                    label: 'ID',
                    field: 'id',
                    sort: 'asc'
                },
                {
                    label: 'Nama',
                    field: 'name',
                    sort: 'asc'
                },
                {
                    label: 'Price',
                    field: 'price',
                    sort: 'asc'
                },
                {
                    label: 'Stock',
                    field: 'stock',
                    sort: 'asc'
                },
                {
                    label: 'Actions',
                    field: 'actions',
                },
            ],
            rows: []
        }

        products.forEach(product => {
            data.rows.push({
                id: product._id,
                name: product.name,
                price: `Rp. ${product.price}`,
                stock: product.stock,
                actions:
                    <Fragment>
                        <Link to={`/admin/produk/${product._id}`} ><FiEdit /></Link>
                        <button className="trash-product"><FiTrash /></button>
                    </Fragment>
            })
        })
        return data
    }

    return (
        <Fragment>
            <MetaData title={'Semua Produk'} />
            <div class="product-list-admin">
                <div className="sidebar-dashboard">
                    <Sidebar />
                </div>
                <div className="content-product-list-admin">
                    <h1>List Produk</h1>
                    {
                        loading ? <Loader /> :
                            <div className="list-product-admin">
                                <MDBDataTable
                                    data={setProducts()}
                                    className="px-3"
                                    bordered
                                    striped
                                    hover
                                />
                            </div>
                    }
                </div>
            </div>
        </Fragment>
    )
}

export default ProductList
