import React, { Fragment, useState, useEffect } from 'react'
import { Link } from 'react-router-dom'

import Loader from '../layout/Loader'
import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux';
import { getOrderDetails, updateOrder, clearErrors } from '../../actions/orderAction'
import { orderConstant } from '../../constants/orderConstant'
import MetaData from '../layout/MetaData';
import Sidebar from './Sidebar'

const ProcessOrder = ({ match }) => {

    const [status, setStatus] = useState('')

    const alert = useAlert()
    const dispatch = useDispatch()

    const { loading, order } = useSelector(state => state.orderDetails)
    const { shippingInfo, orderItems, paymentInfo, user, totalPrice, orderStatus } = useSelector(state => state.product)
    const { error, isUpdated } = useSelector(state => state.order)

    const orderId = match.params.id

    useEffect(() => {

        dispatch(getOrderDetails(orderId))

        if (error) {
            alert.error(error)
            dispatch(clearErrors())
        }
        if (updateError) {
            alert.error(updateError)
            dispatch(clearError())
        }
        if (isUpdated) {
            alert.success('order sudah di ubah')
            dispatch({ type: orderConstant.UPDATE_ORDER_RESET })
        }
    }, [dispatch, alert, error, isUpdated, orderId])

    const updateOrderHandler = (id) => {
        const formData = new FormData();
        formData.set('status', status);

        dispatch(updateOrder(id, formData))
    }

    const shippingDetails = shippingInfo && `${shippingInfo.address}, ${shippingInfo.city}, ${shippingInfo.postialCode}`
    const isPaid = paymentInfo && paymentInfo.status === 'succeeded' ? true : false;

    return (
        <Fragment>
            <MetaData title={`Proses Order # ${order._id}`} />
            <div className="product-list-admin">
                <div className="sidebar-dashboard">
                    <Sidebar />
                </div>
                <div className="content-product-list-admin">
                    <h1>Pesanan {order._id}</h1>
                    {
                        loading ? <Loader /> :
                            <div className="list-product-admin">
                                <div className="pengiriman" >
                                    <h5>Pengiriman</h5>
                                    <div>
                                        <p>Dikirim ke {user && user.name}</p>
                                    </div>
                                    <div>
                                        <p>Telp: {shippingInfo.phoneNo}</p>
                                    </div>
                                    <div>
                                        <p>{shippingDetails}</p>
                                    </div>
                                    <div>
                                        <p>Rp. {totalPrice}</p>
                                    </div>
                                </div>
                            </div>
                    }
                </div>
            </div>
        </Fragment>
    )
}

export default ProcessOrder
