import React, { Fragment, useState, useEffect } from 'react'
import { Link } from 'react-router-dom'

import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux'

// import { login, clearError } from '../../actions/userAction'
import Loader from '../layout/Loader'
import MetaData from '../layout/MetaData'

const Login = ({ history }) => {

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const alert = useAlert()
    const dispatch = useDispatch()

    const { isAuthenticated, error, loading } = useSelector(state => state.auth)

    useEffect(() => {
        if (isAuthenticated) {
            history.push('/')
        }
        if (error) {
            alert.error(error);
            dispatch(clearErrors())
        }
    }, [dispatch, alert, isAuthenticated, error, history])

    return (
        <Fragment>
            {loading ? <Loader /> : (
                <Fragment>
                    <MetaData title={'Masuk'} />
                    <div className="content-app-wrapper">
                        <img src={Loginbg} className="loginbg" alt="masuk" />
                        <div className="kiri">
                            <div className="login-page">
                            </div>
                            <img src={Logobg} className="logo" alt="logo" />
                            <div className="input">
                                <Input
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    label="E-mail"
                                    placeholder="E-mail"
                                />
                                <Input
                                    value={password}
                                    type="password"
                                    onChange={(e) => setpassword(e.target.value)}
                                    label="Password"
                                    placeholder="Password"
                                />
                            </div>
                            <div className="button-klik">
                                <Button
                                    onClick={userLogin}
                                    tittle="masuk"
                                />
                            </div>
                            <div className="keterangan">
                                <Link
                                    to="/xxx">
                                    Lupa Password?
                                </Link>
                                <Link
                                    to="/daftar">
                                    Daftar
                                </Link>
                            </div>
                        </div>
                    </div>
                </Fragment>
            )}
        </Fragment>
    )
}

export default Login
