import React, { Fragment, useState, useEffect } from 'react'

import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux'

import { register, clearError } from '../../actions/userAction'
import MetaData from '../layout/MetaData'
import { avatar } from '../images/Images'

const Register = ({ history }) => {
    const [user, setUser] = useState({
        nama: '',
        email: '',
        password: '',
    })

    const { nama, email, password } = user

    const [avatar, setAvatar] = useState('')
    const [avatarPreview, setAvatarPreview] = useState(avatar)

    const alert = useAlert()
    const dispatch = useDispatch()

    const { isAuthenticated, error, loading } = useSelector(state => state.auth)

    useEffect(() => {
        if (isAuthenticated) {
            history.push('/')
        }
        if (error) {
            alert.error(error);
            dispatch(clearError())
        }
    }, [dispatch, alert, isAuthenticated, error, history])

    const submitHandler = (e) => {
        e.preventDefault()
        const fromData = new FromData()
        fromData.set('nama', nama)
        fromData.set('email', email)
        fromData.set('password', password)
        fromData.set('avatar', avatar)
        dispatch(register(fromData))
    }


    return (
        <Fragment>
            <MetaData title={"Register"} />
        </Fragment>
    )
}

export default Register
