import React, { Fragment, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux';
import { MdArrowBack } from "react-icons/md";
import { getDetailProduct, clearError } from '../../actions/product.actions';
import Loader from '../layout/Loader'
import MetaData from '../layout/MetaData';



const ProductDetails = ({ match }) => {

    const dispatch = useDispatch()
    const alert = useAlert()
    const { loading, error, product } = useSelector(state => state.productDetails)


    useEffect(() => {
        dispatch(getDetailProduct(match.params.id))
        if (error) {
            alert.error(error)
            dispatch(clearError())
        }
    }, [dispatch, alert, error, match.params.id])

    const increaseQty = () => {

    }

    const decreaseQty = () => {

    }

    return (
        <Fragment>
            {loading ? <Loader /> : (
                <Fragment>
                    <MetaData title={product.name} />
                    <div className="ProductDetailContent">
                        <div className="imgproduct">
                            {
                                product.productimg && product.productimg.map(image => (
                                    <div className="img" key={image.public_id}>
                                        <img src={image.url} alt={product.title} />
                                    </div>
                                ))
                            }
                        </div>
                        <div className="productdetail">
                            <div className="text-product">
                                <Link to="/Produk">
                                    <MdArrowBack />
                                </Link>
                                <hr />
                                <p>Product: {product._id}</p>
                                <hr />
                                <h1>{product.name}</h1>
                                <hr />
                                <h5>Status: <span className={product.stock > 0 ? 'greenColor' : 'redColor'}>{product.stock > 0 ? 'Stock' : 'Barang kosong'}</span></h5>
                                <hr />
                                <h3>Rp.{product.price},-</h3>
                                <hr />
                                <div className="stockCounter d-flex">
                                    <span className="btn btn-primary plus" onClick={decreaseQty}>+</span>
                                    <input type="number" className="form-control count d-inline" value="1" readOnly />
                                    <span className="btn btn-light border-dark minus" onClick={increaseQty}>-</span>
                                </div>
                                <hr />
                                <button type="button" className="btn btn-primary d-inline">+ Keranjang</button>
                                <hr />
                                <h4>Deskripsi</h4>
                                <p>{product.desc}</p>
                            </div>
                        </div>
                    </div>
                </Fragment>
            )
            }
        </Fragment>
    )
}

export default ProductDetails
